/* css reset */

* {
    box-sizing: border-box;
}

*:before,
*:after {
    box-sizing: border-box;
}

* {
    margin: 0;
    padding: 0;
}

ul,
ol {
    list-style: none;
}

a {
    color: inherit;
    text-decoration: none;
}

img {
    max-width: 100%;
    max-height: 100%;
}


/* style */

body {
    background: #ffffff;
}

.globalHeader {
    margin: 20px;
}

.headerLogo {
    font-size: 50px;
    display: flex;
    justify-content: center;
    margin-bottom: 24px;
}

@media(min-width:500px) {
    .globalHeader {
        margin: 60px 0 100px;
    }
}

.searchForm {
    display: flex;
    justify-content: space-between;
}

@media (min-width:500px) {
    .searchForm {
        max-width: 400px;
        margin-left: auto;
        margin-right: auto;
    }
}

.searchForm>input {
    width: 100%;
    /* margin-right: 10px; */
    height: 40px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-right: none;
    border-radius: 20px 0 0 20px;
    outline: none;
}

.searchForm:hover>input {
    background: #f1f3f4;
}

.searchForm>button {
    white-space: nowrap;
    padding: 0 12px;
    border: none;
    border-radius: 4px;
    background: #4738d4;
    color: white;
    font-size: 16px;
    border-radius: 0 20px 20px 0;
    cursor: pointer;
}

.searchForm:hover>button {
    background: #291ab1;
}

.globalMain {
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;
}

.siteList {
    margin: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}

@media(min-width:500px) {
    .siteList {
        margin-left: 0;
        margin-right: -25px;
        justify-content: flex-start;
    }
}

.siteList>li {
    margin-bottom: 10px;
}

@media(min-width:500px) {
    .siteList>li {
        margin-right: 25px;
    }
}

.siteList .site {
    width: 112px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    /* background: white; */
    /* border: 1px solid #ddd; */
    border-radius: 4px;
    padding: 20px 0;
    position: relative;
    cursor: pointer;
}

.siteList .site>.logo {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
    text-transform: uppercase;
    border-radius: 50%;
    background: #f1f3f4;
}

.siteList .site>.link {
    font-size: 14px;
    margin-top: 4px;
}

.siteList .site>.close {
    position: absolute;
    right: 10px;
    top: 5px;
    width: 18px;
    height: 18px;
    display: none;
    cursor: default;
    color: #81868a;
    text-align: center;
    line-height: 18px;
}

.siteList .site>.close:hover {
    background: #d3d3d5;
    border-radius: 50%;
    cursor: pointer;
}

.siteList .site:hover,
.siteList .addButton:hover {
    background: #e8e8e9;
}

.siteList .site:hover>.close {
    display: block;
}

.siteList .addButton {
    background: white;
    width: 112px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    padding: 20px 0;
}

.siteList .last {
    margin-right: auto;
}

.siteList .addButton .icon {
    width: 56px;
    height: 56px;
    border-radius: 50%;
    background: #f1f3f4;
}

.siteList .addButton .text {
    font-size: 14px;
    margin-top: 4px;
}

.siteList .addButton .icon-wrapper {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
}
